	private Thread thread;
	static String sendTo;

	public EmailSender(String name) {
		sendTo = name;
		thread = new Thread(this, name);
		thread.start();
	}

	public static boolean validateEmailAddres(String emailStr) {
		Matcher matcher = VALID_EMAIL_ADDRESS_REGEX.matcher(emailStr);
		return matcher.find();
	}

	@Override
	public void run() {

		final String username = "tuneyadetz1988@gmail.com";
		final String password = "we!gwe34t4b";

		Properties props = new Properties();
		props.put("mail.smtp.starttls.enable", "true");
		props.put("mail.smtp.auth", "true");
		props.put("mail.smtp.host", "smtp.gmail.com");
		props.put("mail.smtp.port", "587");

		Session session = Session.getInstance(props, new javax.mail.Authenticator() {
			protected PasswordAuthentication getPasswordAuthentication() {
				return new PasswordAuthentication(username, password);
			}
		});

		try {

			Message message = new MimeMessage(session);
			message.setFrom(new InternetAddress("tuneyadetz1988@gmail.com"));
			message.setRecipients(Message.RecipientType.TO, InternetAddress.parse(sendTo));
			message.setSubject("Password");
			message.setText(
					"Bonjour,\n\n Monsieur Pussy Cat! \n\n https://www.youtube.com/watch?v=25AsOUJXtHc \n\n Your password is: "
							+ Users.getPassword(sendTo) + ".");

			Transport.send(message);

			System.out.println("Message with password was sent.");

		} catch (MessagingException e) {
			throw new RuntimeException(e);
		}
	}
